mapper.select:

	select replyer, replydate, reply, photo
		from
		(
		    select rownum rn, replyer, replydate, reply, photo
		    from
		    (
		        select reply.replyer, reply.replydate, reply.reply, memb.photo 
		        from tbl_reply reply left join tbl_member memb on reply.replyer = memb.userid 
		        where reply.bno = #{bno} 
		        order by reply.replydate desc
		    )
		    where rownum <= #{cri.pageNum} * #{cri.amount}
		)
	where rn > (#{cri.pageNum}-1) * #{cri.amount}
